'use strict';

exports.name = 'FaAssetClass';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    active: {
      type: Boolean,
      required: true
    },
    name: {
      type: String,
      required: true,
      trim: true
    },
    description: {
      type: String,
      trim: true
    },
    tplKey: {
      type: String,
      trim: true,
      pattern: /^L([0-9]{3}|U[0-9]{3})$/
    }
  }, {
    id: false,
    minimize: false
  });

  schema.statics.TOPIC_PREFIX = 'fa.assetClasses';
  schema.statics.BROWSE_LIMIT = 0;

  return schema;
};
