'use strict';

exports.name = 'FaAssetGroup';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    active: {
      type: Boolean,
      required: true
    },
    evalGroup1: {
      type: String,
      required: true,
      trim: true
    },
    evalGroup5: {
      type: String,
      required: true,
      trim: true
    },
    depRate: {
      type: Number,
      required: true,
      min: 0,
      max: 100
    },
    depKey: {
      type: String,
      required: true,
      trim: true
    },
    assetClass: {
      type: String,
      required: true,
      trim: true
    }
  }, {
    id: false,
    minimize: false
  });

  schema.statics.TOPIC_PREFIX = 'fa.assetGroups';
  schema.statics.BROWSE_LIMIT = 0;

  return schema;
};
