'use strict';

const userInfoSchema = require('user/models/userInfoSchema');

exports.name = 'FaCostCenter';

exports.setUp = (app, mongoose) =>
{
  const costCenterSchema = new mongoose.Schema({
    _id: {
      type: String,
      trim: true
    },
    active: {
      type: Boolean,
      required: true
    },
    description: {
      type: String,
      trim: true
    },
    owner: {
      type: userInfoSchema,
      default: null
    }
  }, {
    id: false,
    minimize: false
  });

  costCenterSchema.statics.TOPIC_PREFIX = 'fa.costCenters';
  costCenterSchema.statics.BROWSE_LIMIT = 0;

  mongoose.model(exports.name, costCenterSchema);
};
