'use strict';

exports.name = 'FaDepKey';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    _id: String,
    active: {
      type: Boolean,
      required: true
    },
    name: {
      type: String,
      required: true,
      trim: true
    },
    years: {
      type: Number,
      required: true,
      min: 0,
      max: 100
    },
    months: {
      type: Number,
      required: true,
      min: 0,
      max: 11
    }
  }, {
    id: false,
    minimize: false
  });

  schema.statics.TOPIC_PREFIX = 'fa.depKeys';
  schema.statics.BROWSE_LIMIT = 0;

  return schema;
};
