'use strict';

exports.name = 'FaDestination';

exports.setUp = (app, mongoose) =>
{
  const destinationSchema = new mongoose.Schema({
    active: {
      type: Boolean,
      required: true
    },
    name: {
      type: String,
      required: true,
      trim: true
    },
    description: {
      type: String,
      trim: true
    }
  }, {
    id: false,
    minimize: false
  });

  destinationSchema.statics.TOPIC_PREFIX = 'fa.destinations';
  destinationSchema.statics.BROWSE_LIMIT = 0;

  mongoose.model(exports.name, destinationSchema);
};
