'use strict';

const _ = require('lodash');
const deepEqual = require('deep-equal');
const {transliterate} = require('transliteration');
const lastModifiedPaths = require('h5-mongoose/plugins/lastModifiedPaths');
const userInfoSchema = require('user/models/userInfoSchema');
const collectUsers = require('./collectUsers');

exports.name = 'FaLt';

exports.setUp = (app, mongoose) =>
{
  const STAGE_PROPS = {
    protocol: [
      'stage',
      'protocolDate',
      'inventoryNo',
      'assetName',
      'costCenter',
      'applicant',
      'cause',
      'attachmentFile'
    ],
    verify: [
      'stage',
      'assetName',
      'initialValue',
      'deprecationValue',
      'netValue',
      'economicInitialValue',
      'economicDeprecationValue',
      'economicNetValue',
      'sapNo',
      'attachmentFile'
    ],
    acceptOwner: [
      'stage',
      'committee',
      'committeeAcceptance',
      'attachmentFile'
    ],
    acceptCommittee: [
      'stage',
      'committeeAcceptance',
      'attachmentFile'
    ],
    acceptFinance: [
      'stage',
      'attachmentFile'
    ],
    acceptDepartment: [
      'stage',
      'attachmentFile'
    ],
    acceptDocument: [
      'stage',
      'documentNo',
      'documentDate',
      'buyerName',
      'buyerAddress',
      'saleValue',
      'attachmentFile'
    ],
    record: [
      'stage',
      'accountingNo',
      'attachmentFile'
    ],
    finished: [
      'mergeInventoryNo',
      'mergeLineSymbol',
      'mergeType',
      'protocolDate',
      'documentDate',
      'inventoryNo',
      'assetName',
      'costCenter',
      'committee',
      'applicant',
      'cause',
      'initialValue',
      'deprecationValue',
      'netValue',
      'economicInitialValue',
      'economicDeprecationValue',
      'economicNetValue',
      'buyerName',
      'buyerAddress',
      'saleValue',
      'sapNo',
      'accountingNo',
      'attachmentFile'
    ],
    cancelled: [
      'stage'
    ]
  };

  const attachmentSchema = new mongoose.Schema({
    hash: String,
    type: String,
    size: Number,
    name: String
  }, {
    _id: false
  });

  const changeSchema = new mongoose.Schema({
    date: {
      type: Date,
      required: true
    },
    user: userInfoSchema,
    data: {},
    comment: {
      trim: true,
      type: String,
      default: ''
    }
  }, {
    _id: false,
    minimize: false
  });

  const ltSchema = new mongoose.Schema({
    createdAt: {
      type: Date,
      required: true
    },
    createdBy: {
      type: userInfoSchema,
      required: true
    },
    updatedAt: {
      type: Date,
      default: null
    },
    updatedBy: {
      type: userInfoSchema,
      default: null
    },
    stage: {
      type: String,
      required: true,
      enum: [
        'protocol',
        'verify',
        'acceptOwner',
        'acceptCommittee',
        'acceptFinance',
        'acceptDepartment',
        'acceptDocument',
        'record',
        'finished',
        'cancelled'
      ]
    },
    stageChangedAt: {
      protocol: Date,
      verify: Date,
      acceptOwner: Date,
      acceptFinance: Date,
      acceptDepartment: Date,
      acceptDocument: Date,
      record: Date
    },
    stageChangedBy: {
      protocol: userInfoSchema,
      verify: userInfoSchema,
      acceptOwner: userInfoSchema,
      acceptFinance: userInfoSchema,
      acceptDepartment: userInfoSchema,
      acceptDocument: userInfoSchema,
      record: userInfoSchema
    },
    kind: {
      type: String,
      required: true,
      enum: ['scrap', 'merge', 'sale', 'other']
    },
    protocolNo: {
      type: String,
      default: ''
    },
    protocolNoInc: {
      type: Number,
      default: 0
    },
    documentNo: {
      type: String,
      default: ''
    },
    documentNoInc: {
      type: Number,
      default: 0
    },
    date: Date,
    protocolDate: {
      type: Date,
      required: true
    },
    documentDate: {
      type: Date,
      default: null
    },
    inventoryNo: {
      type: String,
      trim: true,
      default: ''
    },
    assetName: {
      type: String,
      trim: true,
      default: ''
    },
    assetNameSearch: String,
    costCenter: {
      type: String,
      ref: 'FaCostCenter',
      default: null
    },
    applicant: {
      type: userInfoSchema,
      default: null
    },
    committee: [userInfoSchema],
    committeeAcceptance: {},
    cause: {
      type: String,
      trim: true,
      default: ''
    },
    initialValue: {
      type: Number,
      min: 0,
      default: 0
    },
    deprecationValue: {
      type: Number,
      min: 0,
      default: 0
    },
    netValue: {
      type: Number,
      default: 0
    },
    economicInitialValue: {
      type: Number,
      min: 0,
      default: 0
    },
    economicDeprecationValue: {
      type: Number,
      min: 0,
      default: 0
    },
    economicNetValue: {
      type: Number,
      default: 0
    },
    mergeInventoryNo: {
      type: String,
      trim: true,
      default: ''
    },
    mergeLineSymbol: {
      type: String,
      trim: true,
      default: ''
    },
    mergeType: {
      type: String,
      enum: [null, 'full', 'partial'],
      default: null
    },
    buyerName: {
      type: String,
      trim: true,
      default: ''
    },
    buyerNameSearch: String,
    buyerAddress: {
      type: String,
      trim: true,
      default: ''
    },
    saleValue: {
      type: Number,
      min: 0,
      default: 0
    },
    sapNo: {
      type: String,
      trim: true,
      default: ''
    },
    accountingNo: {
      type: String,
      trim: true,
      default: ''
    },
    attachmentFile: {
      type: attachmentSchema,
      default: null
    },
    users: [String],
    changes: [changeSchema]
  }, {
    id: false,
    minimize: false
  });

  ltSchema.statics.TOPIC_PREFIX = 'fa.lt';
  ltSchema.statics.PRIVILEGE_PREFIX = 'FA:LT';
  ltSchema.statics.BROWSE_LIMIT = 100;
  ltSchema.statics.SEARCH_PROPS = ['assetName', 'buyerName'];
  ltSchema.statics.USER_PROPS = ['createdBy', 'applicant', 'committee'];

  ltSchema.index({createdAt: -1});
  ltSchema.index({date: -1});
  ltSchema.index({kind: 1});
  ltSchema.index({stage: 1});
  ltSchema.index({protocolNo: 1});
  ltSchema.index({documentNo: 1});
  ltSchema.index({inventoryNo: 1});
  ltSchema.index({sapNo: 1});
  ltSchema.index({accountingNo: 1});
  ltSchema.index({costCenter: 1});
  ltSchema.statics.SEARCH_PROPS.forEach(prop => ltSchema.index({[`${prop}Search`]: 1}));

  ltSchema.pre('save', function(next)
  {
    this.date = this.documentDate || this.protocolDate;

    ltSchema.statics.SEARCH_PROPS.forEach(prop =>
    {
      if (this.isNew || this.isModified(prop))
      {
        this[`${prop}Search`] = this.constructor.prepareSearchText(this[prop]);
      }
    });

    collectUsers(this, ['createdBy', 'applicant', 'committee']);

    next();
  });

  ltSchema.plugin(lastModifiedPaths);

  ltSchema.statics.prepareSearchText = function(value)
  {
    return transliterate(value, {unknown: ''})
      .replace(/[^A-Za-z0-9]+/g, '')
      .toUpperCase();
  };

  ltSchema.methods.applyChanges = function(input, updatedBy)
  {
    this.updatedAt = new Date();
    this.updatedBy = updatedBy;

    const currentStage = this.stage;
    const comment = input.comment || '';

    input.stage = input.newStage || currentStage;

    if (input.stage !== currentStage)
    {
      const stages = Object.keys(STAGE_PROPS);
      const currentI = stages.indexOf(currentStage);
      const newI = stages.indexOf(input.stage);

      if (newI > currentI)
      {
        this.stageChangedAt[currentStage] = this.updatedAt;
        this.stageChangedBy[currentStage] = this.updatedBy;
      }
      else if (newI < currentI)
      {
        this.stageChangedAt[input.stage] = this.updatedAt;
        this.stageChangedBy[input.stage] = this.updatedBy;
      }
      else if (input.newStage !== 'cancelled')
      {
        delete input.stage;
      }
    }

    const changes = this.compareProperties(_.pick(input, STAGE_PROPS[currentStage]));

    if (_.isEmpty(changes) && _.isEmpty(comment))
    {
      return false;
    }

    this.changes.push({
      date: this.updatedAt,
      user: updatedBy,
      data: changes,
      comment
    });

    return true;
  };

  ltSchema.methods.compareProperties = function(input)
  {
    const changes = {};

    _.forEach(input, (value, key) => { this.compareProperty(key, input, changes); });

    return changes;
  };

  ltSchema.methods.compareProperty = function(property, input, changes)
  {
    let oldValue = this[property];
    let newValue = input[property];

    if (_.isObject(oldValue) && _.isFunction(oldValue.toObject))
    {
      oldValue = oldValue.toObject();
    }

    if (_.isObject(newValue) && _.isFunction(newValue.toObject))
    {
      newValue = newValue.toObject();
    }

    if (oldValue instanceof mongoose.Types.ObjectId)
    {
      oldValue = oldValue.toString();
    }

    if (_.isString(newValue))
    {
      newValue = newValue.trim();

      if (/date/i.test(property))
      {
        newValue = new Date(newValue);
      }
    }

    if (deepEqual(newValue, oldValue, {strict: true}))
    {
      return false;
    }

    changes[property] = [oldValue, newValue];
    this[property] = newValue;

    return true;
  };

  ltSchema.methods.isCommitteeMember = function(userId)
  {
    console.log(typeof userId, userId, this.committeeAcceptance);
    return !!this.committeeAcceptance && !!this.committeeAcceptance[userId];
  };

  mongoose.model(exports.name, ltSchema);
};
