'use strict';

module.exports = (app, {FaLt}, req, res, next) =>
{
  let noProperty = 'documentNo';

  if (/^PZ/.test(req.query.rid))
  {
    noProperty = 'protocolNo';
  }

  FaLt.findOne({[noProperty]: req.query.rid}, {_id: 1}).lean().exec((err, model) =>
  {
    if (err)
    {
      return next(err);
    }

    if (model)
    {
      return res.json(model._id);
    }

    return res.sendStatus(404);
  });
};
