'use strict';

exports.name = 'BrowserError';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    time: Date,
    user: {},
    headers: {},
    versions: {},
    browser: {},
    error: {},
    resolved: {
      type: Boolean,
      default: false
    }
  }, {
    id: false,
    versionKey: false
  });

  schema.statics.TOPIC_PREFIX = 'logs.browserErrors';
  schema.statics.BROWSE_LIMIT = 100;

  schema.index({time: -1});
  schema.index({resolved: 1, time: -1});

  return schema;
};
