'use strict';

exports.name = 'Ping';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    time: Date,
    path: String,
    query: String,
    headers: {},
    user: String,
    ip: String
  }, {
    id: false
  });

  schema.statics.TOPIC_PREFIX = 'pings';

  schema.index({time: -1});
  schema.index({path: 1, time: -1});
  schema.index({'user.id': 1, time: -1});
  schema.index({'user.ip': 1, time: -1});

  return schema;
};
