'use strict';

exports.name = 'Printer';

exports.setUp = (app, mongoose) =>
{
  const schema = new mongoose.Schema({
    name: {
      type: String,
      trim: true
    },
    label: {
      type: String,
      required: true,
      trim: true
    },
    tags: [String],
    special: String
  }, {
    id: false
  });

  schema.statics.TOPIC_PREFIX = 'printing.printers';
  schema.statics.BROWSE_LIMIT = 1000;

  return schema;
};
